mapper.insert:

    SELECT vacation_increment.nextval FROM dual

INSERT INTO vacation (
    id, start_date, duration, approval, username
)
VALUES (
    #{id}, #{startDate}, #{duration}, #{approval}, #{username}
    )

mapper.update:
UPDATE vacation

     start_date=#{startDate},
     duration=#{duration},
     approval=#{approval}

WHERE UPPER(username) = UPPER(#{username})
AND   id = #{id}
